import React, {useState,useEffect} from "react";
import { useNavigate } from "react-router-dom";
import { getFeedbacks } from "../services/feedbackServices";

function FeedbackDashboard(){
    const[feedbacks, setFeedbacks] = useState([]);
    const[sortBy, setSortBy] = useState('');
    const[category, setCategory] = useState('');
    const navigate = useNavigate();

    useEffect(()=>{
        fetchFeedbacks();
    },[sortBy,category]);

    const fetchFeedbacks = async()=>{
        const {data} = await getFeedbacks(sortBy, category);
        setFeedbacks(data);
    };

    const handleBack = () =>{
        navigate('/');
    }

    return(
        <div>

            <button onClick={handleBack}>Back to Feedback Form</button>

            <div>
                <select onChange={(e)=>setSortBy(e.target.value)}>
                    <option value="">Sort By</option>
                    <option value="asc">Oldest First</option>
                    <option value="desc">Newest First</option>
                </select>

                <select onChange={(e)=>setCategory(e.target.value)}>
                    <option value="">All Categories</option>
                    <option value="Suggestion">Suggestion</option>
                    <option value="Bug Report">Bug Report</option>
                    <option value="Feature Request">Feature Request</option>
                </select>
            </div>
            <ul>
                {feedbacks.map((f)=>(
                    <li key={f.id}>
                        <strong>{f.username}</strong> ({f.email}) [{f.category}] <br/>
                        {f.feedback}<br/>
                        <small>{new Date(f.createdAt).toLocaleString()}</small>
                    </li>
                ))}
            </ul>
        </div>
    )
}
export default FeedbackDashboard;
